<div id="msg">	<?php		if(@count($media)>0):			$cur=isset($current)?$current:$media[0][0]['Fone']['id'];	?>	<h4>Reference</h4>		Currently Viewing : 		<select onchange="changeMedia(this)">		<?php			for($i=0;$i<count($media);$i++):				$m=$media[$i][0];		?>			<option 			<?php 				if($m['Fone']['id']==$cur):					echo "selected ";					$play=$m;				endif; 				echo "value = ".$m['Fone']['id'];?>>				<?php echo $m['Fone']['title'];?>			</option>			<?php			endfor;		?>		</select>		<div id="mediaOut">		<?php			if($play['Fone']['type']=="video"):		?>			<iframe src="<?php echo $play['Fone']['filename'];?>" width="320" height="190" frameborder="0">			</iframe>		<?php			elseif($play['Fone']['type']=="image"):				echo "<a href=".ip."utopia/public/image/".$play['Fone']['filename']." target='_blank'>".$html->insertImgWH($play['Fone']['filename'],$play['Fone']['title'],'320','190')."</a>";			else:				echo "<a href=".ip."utopia/public/other/".$play['Fone']['filename']." target='_blank'>".$play['Fone']['filename']."</a>";			endif;		?>		</div>	<?php		endif;	?></div>